      Generators can be somewhat tricky to type check. For example:



<?hh
function gen() {
  yield 1;
  yield &quot;a&quot;;
}

      The following two examples are correctly typed:



<?hh
function gen(): Continuation<int> {
  yield 1;
  yield 2;
  yield 3;
}

function foo(): void {
  foreach (gen() as $x) {
    echo $x, "\n";
  }
}




<?hh
class MultiplyByTwo extends Preparable<MultiplyByTwo> implements IUseGeneratorInLegacyPreparable {
  private int $n;
  private ?int $result = null;

  public function __construct(int $n) {
    $this->n = $n;
  }

  public function gen(): Awaitable<void> {
    $this->result = $this->n * 2;
    return gen_void();  // Return a wait handle that does nothing
  }

  public function getResult(): int {
    return nullthrows($this->result);
  }
}

class MultiplyByThree extends Preparable<MultiplyByThree> implements IUseGeneratorInLegacyPreparable {
  private int $n;
  private ?int $result = null;

  public function __construct(int $n) {
    $this->n = $n;
  }

  public function gen(): Awaitable<void> {
    $this->result = $this->n * 3;
    return gen_void();  // Return a wait handle that does nothing
  }

  public function getResult(): int {
    return nullthrows($this->result);
  }
}

/**
 * Computes 6*n^2 using Preparables.
 * A preparable is used to compute 3*n, another one for 2*n and the results are combined into 6*n^2.
 */
class MultiplyAndSquare extends Preparable<MultiplyAndSquare> implements IUseGeneratorInLegacyPreparable {
  private int $n;
  private ?int $result = null;

  public function __construct(int $n) {
    $this->n = $n;
  }

  public function gen(): Awaitable<void> {
    list(
      $by_two,
      $by_three,
    ) = yield wait_forva(
      new MultiplyByTwo($this->n),
      new MultiplyByThree($this->n),
   );
    $this->result = $by_two->getResult() * $by_three->getResult();
  }

  public function getResult(): int {
    return nullthrows($this->result);
  }
}
